mapper.select:
SELECT t1.`id`, `excel_file_id`, `sheet_name`, `folder_id`, t2.`folder_name`, `category_flag`, `remark`, `table_name`, table_clumns, table_rows, CONCAT_WS('/',table_rows,table_clumns,NULL) AS rows_clumns, DATE_FORMAT(update_time,'%Y-%m-%d %h:%m%:%s') AS update_time, t1.`user_id` FROM `ds_excel_sheet_info` t1 LEFT JOIN gb_folder_info t2 ON(t1.folder_id=t2.id)
and t1.`id` = #{id}
and folder_id = #{folderId}
and sheet_name like concat(concat('%', #{sheetName}), '%')

mapper.update:
UPDATE `ds_excel_sheet_info`
sheet_name=#{sheetName}, folder_id=#{folderId}, category_flag=#{categoryFlag}, remark=#{remark}, table_name=#{tableName}, table_clumns=#{tableClumns}, table_rows=#{tableRows}, update_time=#{updateTime}, user_id=#{userId},
WHERE id = #{id};

mapper.delete:
DELETE
FROM ds_excel_sheet_info


		and id = #{id}


		and table_name = #{tableName}
